import { Box, FormControl, FormErrorMessage, FormHelperText, FormLabel, Heading ,Input} from '@chakra-ui/react'
import React, { useState } from 'react'
import { useContext } from 'react'
import { AppContex } from '../Context/AppContext'

export const Home = () => {
    //const {value , handleCount}= useContext(AppContex)
//   return (
//     <Box>
//     <Heading> Derma </Heading>
  
  const [input, setInput] = useState('')

  const handleInputChange = (e) => setInput(e.target.value)

  const isError = input === ''
//Input ,FormHelperText ,FormErrorMessage ,FormControl
  return (
    <FormControl isInvalid={isError}>
      <FormLabel>Email</FormLabel>
      <Input
        type='email'
        value={input}
        onChange={handleInputChange}
      />
      {!isError ? (
        <FormHelperText>
          Enter the email you'd like to receive the newsletter on.
        </FormHelperText>
      ) : (
        <FormErrorMessage>Email is required.</FormErrorMessage>
      )}
    </FormControl>
  )
}
