import { StarIcon } from '@chakra-ui/icons'
import { Button, Grid, GridItem, Image, Text } from '@chakra-ui/react'
import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import styles from "../Body/Body.module.css"
import { QuickBuyBottom } from './QuickBuyBottom'

export const QuickBuy = () => {
    const [data , setData]= useState([])
   const i=5;
    const getData =()=>
    {
        fetch(" http://localhost:8000/QuickBuyData")
        .then((res)=>res.json())
        .then((data)=>
        {
            setData(data)
            console.log(data)
        })
    }
    useEffect(()=>
    {
        getData()
    },[])
  return (
    <div>
         <Grid templateColumns='repeat(5, 1fr)' gap={4} border="1px solid black" w="95%" m="auto" h="350px">
            {
                data.map((elem)=>
                (
                     <GridItem className={styles.shadow} borderRadius={"12px"} >
                       <Image borderRadius={"12px"} w="92%" h="60%" src={elem.img} m="auto"/>
                         <Text>   {elem.title} </Text>
                         

                       {/* <br /> */}
                        <>  â‚¹ {elem.price}</>
                        <br></br>
                        {new Array(5).fill("").map((item, i) => (
                        
                            <StarIcon key={i}
                              color={i < elem.rating ? "blue.500" : "gery.200"}
                              
                            />
                          ))}
                            
                        
                        <br />

                        <Button bg="black" color="white">quick Buy</Button>


                        </GridItem>
                       
                ))
            }

         </Grid>
         
         <QuickBuyBottom />
    </div>
   
  )
}
